package dayLecture.day0901;

public class Ex11DataType {

    public static void main(String[] args) {
        // TODO Auto-generated method stub
        char a = 'A';
        System.out.println(a + 2);
        System.out.println((char) (a + 2));
        System.out.println("^^:" + 5 + 3);
        System.out.println("^^:" + (5 + 3));
    }

}
